import {useEffect, useState} from 'react'
import moment from 'moment'
import Select from './components/Select'
import {convert, getCurrencies} from './utils/currency'

const App = () => {
    const [baseCurrency, setBaseCurrency] = useState<string>('eur')
    const [targetCurrency, setTargetCurrency] = useState<string>('usd')
    const [amount, setAmount] = useState<any>('')
    const [result, setResult] = useState<any>('')
    const options = getCurrencies()
    useEffect(() => {
        convert(baseCurrency, targetCurrency, amount).then(res => {
            setResult(res)
        })
    }, [baseCurrency, targetCurrency, amount])
    const swap = () => {
        let tmp = baseCurrency
        setBaseCurrency(targetCurrency)
        setTargetCurrency(tmp)
    }
    return (
        <>
            <div className="main">
                <div className="container py-4 sm:py-16">
                    <h2 className="text-slate-700 text-2xl sm:text-3xl font-semibold">Currency Converter</h2>
                    <span
                        className="text-slate-500 text-xs font-medium">{'Today ' + moment().format('Y-M-DD HH:mm')}</span>
                    <div className="flex flex-col items-center sm:flex-row gap-4 sm:gap-10 mt-6 sm:mt-10">
                        <div className="flex-1 bg-white p-4 shadow rounded-md">
                            <Select label="From" value={baseCurrency} options={options} stateChanger={setBaseCurrency}/>
                            <input type="text" className="w-full text-3xl sm:text-6xl mt-4 sm:mt-10"
                                   placeholder={baseCurrency.toUpperCase()} value={amount}
                                   onChange={(e) => setAmount(e.target.value)}/>
                        </div>
                        <button onClick={swap}
                                className="flex justify-center items-center w-12 h-12 bg-indigo-600 hover:ring-[12px] hover:ring-indigo-100 rounded-full duration-75">

                        </button>
                        <div className="flex-1 bg-white p-4 shadow rounded-md">
                            <Select label="To" value={targetCurrency} options={options}
                                    stateChanger={setTargetCurrency}/>
                            <input type="text" className="w-full text-3xl sm:text-6xl mt-4 sm:mt-10"
                                   placeholder={targetCurrency.toUpperCase()} value={result !== 0 ? result : ''}
                                   readOnly/>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default App
